select * ,date_format(create_time,'%Y-%m-%d %H:%i:%s') as createTime ,date_format(update_time,'%Y-%m-%d %H:%i:%s') as updateTime ,concat(doc_url, '&flowId=',flow_id) as docUrl from flow where delete_flag = 0
